import { Component } from '@angular/core';
import { UntypedFormBuilder, UntypedFormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { NzModalService } from 'ng-zorro-antd/modal';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent {
  user: never[] | undefined;
  name: any;

  constructor(
    private modal: NzModalService,
    private fb: UntypedFormBuilder,

    private route: Router,
    private router: ActivatedRoute
  ) {}
  z = [];
  tooltips = [];
  value = 5;
  value2 = 4;
  fallback =
    'data:image/png;base64,';

  date = Date();
  time = new Date();
  isVisible = false;
  validateForm!: UntypedFormGroup;
  controlArray: Array<{ index: number; show: boolean }> = [];
  isCollapse = true;
  totalexpense: any = [];
  theme = true;

  // uploading = false;
  // fileList: NzUploadFile[] = [];

  showModal(): void {
    this.isVisible = true;
  }

  handleOk(): void {
    console.log('Button ok clicked!');
    this.isVisible = false;
  }

  handleCancel(): void {
    console.log('Button cancel clicked!');
    this.isVisible = false;
  }

  toggleCollapse(): void {
    this.isCollapse = !this.isCollapse;
    this.controlArray.forEach((c, index) => {
      c.show = this.isCollapse ? index < 6 : true;
    });
  }

  resetForm(): void {
    this.validateForm.reset();
  }

  profile() {
    this.route.navigate(['/dashboard/profile'], {
      relativeTo: this.router,
    });
  }

  paichart() {
    this.route.navigate(['/dashboard/progration'], {
      relativeTo: this.router,
    });
  }

  userlist() {
    this.route.navigate(['/dashboard/userlist'], {
      relativeTo: this.router,
    });
  }
  productlist() {
    this.route.navigate(['/dashboard/productlist'], {
      relativeTo: this.router,
    });
  }

  ngOnInit(): void {
    this.validateForm = this.fb.group({
      income: [],
      expanse: [],
    });
    // this.info();
  }

  count() {
    this.totalexpense.push(
      { expanse: this.validateForm.value.expanse },
      { income: this.validateForm.value.income }
    );
    console.log(this.totalexpense);
  }
  // this.totalexpense.count(data).subscribe((result: any) => { console.warn(result) })
  // }

  // info(): void {
  //   let data: any = localStorage.getItem('email');
  //   this.dashboard.info({ email: data }).then((res: any) => {
  //     console.log(res);
  //     const modal = this.modal.success({
  //       nzTitle: res.body.userName,
  //       nzContent: res.body.email,
  //     });
  //   });
  // }
}
